(window.webpackJsonp=window.webpackJsonp||[]).push([[78],{1155:function(_,v,t){"use strict";t.r(v);var a=t(4),s=Object(a.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("最近，我又密集参加了几场研讨会，再次听到了关于数字化战略价值的诸多说法。这些研讨会的分享者主要有四类。")]),_._v(" "),v("p",[_._v("第一类是"),v("strong",[_._v("政府部门的代表")]),_._v("。他们指出，数字化是必然趋势，对企业的未来生存发展至关重要。不过，他们没有告诉我们，数字化到底是什么？怎么做？能够产生什么价值？")]),_._v(" "),v("p",[_._v("第二类是"),v("strong",[_._v("软件厂商")]),_._v("。这些人说，他们的 ERP（企业资源规划）、CRM（客户关系管理）软件是企业数字化的最佳选择。可是，这些软件已经面市一二十年，怎么突然换了个身份，从支持业务操作的软件变成了“让企业变得更聪明的软件”？")]),_._v(" "),v("p",[_._v("第三类是服务于**大型企业的 IT****主管**。他们大都展示了一个非常复杂的架构图，里面既有门店收银、天猫、京东、移动 App、官网商城等各种应用构成的前台，也有 ERP、WMS（仓库管理系统）、TMS（运输管理系统）、数据仓储等诸多熟悉名词汇聚的后台，还有他们最喜欢谈论的能够整合后台数据、赋能前台运营的中台。")]),_._v(" "),v("p",[_._v("第四类是来自不同行业的"),v("strong",[_._v("企业职能主管")]),_._v("。制造行业的分享者说，数字化可以提升产品生产效率和质量。零售行业的则说，数字化可以帮助获客，做好客户关系管理。显然，他们都是从各自职能的视角出发的，好像安装了相关系统，就完成了企业的数字化。")]),_._v(" "),v("p",[_._v("可见，上述各类分享者关于数字化或智能化的说法，就像是盲人摸象，每个人讲的好像都很有道理，可是又不完全正确。那么，到底有没有一个比较好的战略思考框架，可以帮助企业审视自己的数字化做到了什么程度，还有没有其他更有价值的应用机会？")]),_._v(" "),v("h1",{attrs:{id:"_01-企业数字化是分阶段、分层次的"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_01-企业数字化是分阶段、分层次的"}},[_._v("#")]),_._v(" 01 "),v("strong",[_._v("企业数字化是分阶段、分层次的")])]),_._v(" "),v("p",[_._v("为了回答这个问题，我对国内外十余家大型企业的数字化路径进行了研究，归纳出了一个企业数字化五阶段模型。")]),_._v(" "),v("p",[v("img",{attrs:{src:t(962),alt:"图片"}})]),_._v(" "),v("p",[_._v("利用这个战略思考框架，你可以对自己企业所处的数字化阶段或程度做出判断，同时，可以清楚看到现阶段还有哪些不足，以及接下来还可以朝哪些方向进一步提升，为企业创造更大的价值。")]),_._v(" "),v("h2",{attrs:{id:"第一阶段-数据连通"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#第一阶段-数据连通"}},[_._v("#")]),_._v(" "),v("strong",[_._v("第一阶段：数据连通")])]),_._v(" "),v("p",[v("strong",[_._v("数据连通就是解决目前企业普遍存在的信息孤岛或应用孤岛的问题，也是企业数字化、智能化的基础工作。")])]),_._v(" "),v("p",[_._v("比如，在石化、炼钢等连续型生产企业，生产流程中的很多过程控制系统（PCS）会实时收集各个生产单元的相关数据，如炼钢炉内铁水的温度、压力、杂质含量等。")]),_._v(" "),v("p",[_._v("在机械加工行业，需要了解生产线上加工设备的相关数据，如刀具、钻具等的加工精度、耗损情况等。在零售行业，需要实时掌握终端的产品销售等数据。")]),_._v(" "),v("p",[_._v("在仓储物流行业，需要实时更新库存进出等数据。在农业生产中，通过物联网（IoT）随时掌握农地和水塘的湿度、温度、含氧量等数据。")]),_._v(" "),v("p",[_._v("所有这些通过各种方式采集的数据，会实时汇集到企业总部的控制室或老板的办公室，并展示在一个非常壮观的数据看板上。没错，如果你的企业完成了数据连通，最终就会展现在数据看板上。")]),_._v(" "),v("p",[_._v("虽然我并不反对将数据看板视为企业数字化的范畴，但是，当很多企业兴奋地向我演示它们的数据看板，并且告诉我这就是它们数字化的成果时，我心里嘀咕着：这只是企业数字化最初级的工作，还完全不涉及任何智能化。")]),_._v(" "),v("h2",{attrs:{id:"第二阶段-运营监控"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#第二阶段-运营监控"}},[_._v("#")]),_._v(" "),v("strong",[_._v("第二阶段：运营监控")])]),_._v(" "),v("p",[v("strong",[_._v("当数据汇集到企业总部时，企业需要立即对这些数据进行比对分析，以掌握业务运营过程是否发生异常现象。")])]),_._v(" "),v("p",[_._v("比如，在炼钢厂，炼钢炉内的铁水在温度、压力等指标上都是有一定标准的，数值过高或过低都会影响产品的质量。")]),_._v(" "),v("p",[_._v("在机械加工中，钻孔的深度和位置需要实时与产品规格做比较，以判断加工精度是否达标。")]),_._v(" "),v("p",[_._v("在零售行业，需要将单位小时的销售数字做同比和环比分析，这样才能判断当天的销售是否正常。")]),_._v(" "),v("p",[_._v("很多企业也会将这些异常信息展示在数据看板上。这时候，数据看板就成了展现企业初步智能化的可视化工具。你可能会问，都是将信息呈现在数据看板上，第二阶段与第一阶段的关键差异在哪里呢？")]),_._v(" "),v("p",[_._v("很简单，**第二阶段开始具备发现异常的能力。**在第一阶段，数据看板只是展现当前的业务运营状况，并没有处理是否异常的相关问题。要判断是否异常，可以用流程、工艺的标准作为比对基础，比如在机械加工、农业生产中的应用场景。")]),_._v(" "),v("p",[_._v("但是，有些行业，比如零售行业、仓储物流行业等，没有流程、工艺标准，这时候，可以跟历史数字或计划数字进行比对。所以，第二阶段比第一阶段多做的是比对数据，发现差异，并提出警示。如果没有做到这些，你的企业就仍然停留在第一阶段。")]),_._v(" "),v("h2",{attrs:{id:"第三阶段-自主优化"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#第三阶段-自主优化"}},[_._v("#")]),_._v(" "),v("strong",[_._v("第三阶段：自主优化")])]),_._v(" "),v("p",[_._v("**自主优化开始涉及更多的智能化处理。**在第二阶段的数据比对中，一旦系统发现当前业务运营存在异常，需要立即做出两个动作："),v("em",[_._v("1.")]),_._v(" 进行诊断，找出原因；"),v("em",[_._v("2.")]),_._v(" 制定调整方案或处方行动。")]),_._v(" "),v("p",[_._v("系统如何能够自我诊断并提出调整方案呢？关键在于知识的积累。")]),_._v(" "),v("p",[_._v("还是以炼钢厂为例，铁矿石投入炼钢炉加热，到了该出铁水的时间，但对炼钢炉传感器所采集的数据进行比对后，发现炉内铁水的温度或杂质含量没有达标，原因是这次加工冶炼的铁矿石成分跟以往不同。这时候，系统可能会给出这样的调整方案：延长加热时间，或者添加某些助溶剂去除杂质。")]),_._v(" "),v("p",[_._v("同样，在机械加工中，会通过传感器测量钻孔的位置和深度，如果传感器回传的数据跟标准存在误差，系统就会提示调整钻头的位置，再进行加工。")]),_._v(" "),v("p",[_._v("生产制造中的问题判定和调整方案，因为有固定的操作标准作为依据，相对来说容易实现自主优化。而"),v("strong",[_._v("商业上的自动化则较难标准化。这时候，系统需要对业务行为的因果关系进行解构，了解数值变化中的机理，才能做出正确的判断。")])]),_._v(" "),v("p",[_._v("例如，对线上线下的销售成绩进行监控，一般都可以采用电商行业的常用指标——GMV（商品交易总额），进一步拆解的话，就是 GMV = UV × CVR × ASP。")]),_._v(" "),v("p",[_._v("其中，UV 表示来客数，就是有多少客户进入线下门店，或者有多少客户访问线上店铺；CVR 表示转化率，就是进入门店或访问店铺的客户中，有多少人最后完成了购买行为；ASP 表示平均客单价，就是有购买行为的客户平均花了多少钱。")]),_._v(" "),v("p",[_._v("假设要监控上午 10 点到 11 点线上或线下的营业数据，发现同昨天相比，今天这个时段的 GMV 下滑了。")]),_._v(" "),v("p",[_._v("如果进一步分析发现，CVR 和 ASP 没有变化，UV 降低了，那么基本可以判断出今天该时段 GMV 下滑的主要原因是来客数减少了。如果要进行干预，系统可能给出的方案是，对于线下场景，让店员到店外发传单，提高引流到店的人数；对于线上场景，加大关键字搜索等互联网广告的投放。")]),_._v(" "),v("p",[_._v("如果分析发现是 CVR 出现下降，那么无论是线上还是线下销售场景，可能的调整方案都是降低主要陈列产品的价格，或者更换用于引流的“钩子”产品，以提高客户购买的意愿，这样 CVR 才能回升。")]),_._v(" "),v("p",[_._v("如果是 ASP 出现下降，可能就需要推出满减活动或捆绑套餐，让客户能够一次性购买更多产品，提高客单价。上述这些方案一旦实行，数据系统仍需要持续监控销售状况，以确定销售下滑的问题得到解决。")]),_._v(" "),v("p",[_._v("在零售行业的客户运营管理上，这种自主优化也极为重要。我曾管理一家大型连锁便利店企业，我们拥有 6000 万会员，需要制定一些规则来监控客户行为，一旦发现某些客户有流失倾向，就需要立即采取行动。")]),_._v(" "),v("p",[_._v("从 6000 万会员数量来看，我们不可能让市场营销人员每天手工做分析，找出哪些人正在流失。我们采取的做法是，将每位会员最近来店的时间间隔与过去 3 个月平均时间间隔进行比对。")]),_._v(" "),v("p",[_._v("比如，某位会员过去 3 个月来店 12 次，就表示他差不多每周都会来店 1 次。如果他最近一次来店时间已经过去 7 天，他应该要来店了，却没有来，我们就会分析他的消费类型。")]),_._v(" "),v("p",[_._v("如果确定他经常会消失一两周做出差旅行，那么这次过了一周还没来店，也算正常。如果他不是经常出差旅行，我们可能就需要给他发送激活邀请，比如告诉他今天来店买咖啡可以获得 2 元优惠，或者今天中午 12 点半后来店买盒饭可以七五折优惠。")]),_._v(" "),v("p",[_._v("至于到底建议他买咖啡还是盒饭，以及应当给予多少优惠，就取决于在过往消费记录中，他对哪种商品的消费更为频繁，以及他对价格的敏感度如何。")]),_._v(" "),v("p",[_._v("当然，无论是数据采集、异常判断，还是提出方案、效果反馈，这些操作都是数据系统自动处理完成的。如果你们的业务流程也能够做到这样的智能化处理与经验学习，就说明你们在业务环节上已经达到了自主优化的程度。")]),_._v(" "),v("h2",{attrs:{id:"第四阶段-智能企业"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#第四阶段-智能企业"}},[_._v("#")]),_._v(" "),v("strong",[_._v("第四阶段：智能企业")])]),_._v(" "),v("p",[_._v("在前一阶段，企业数字化的重点是，针对企业的生产制造、市场营销、物流配送等环节中的一部分进行高效的数据采集和应用，目的是达到局部的最优化。")]),_._v(" "),v("p",[_._v("比如，在企业的生产制造环节，不同工作单元或工作站通过实时数据采集、数据分析、调整反馈，实现更好的产品质量、更高效的产出。但是，这些操作都限制在比较小的范围，无法让整个生产流程做到全面最优化。怎么办？")]),_._v(" "),v("p",[_._v("我们需要将工厂内所有生产单元的过程控制数据集中存放到云端服务器，进行整合应用，从而实现整个工厂的最优化。")]),_._v(" "),v("p",[_._v("同理，我们把企业市场营销环节的客户标签、客户行为变化、精准营销案例等数据，销售环节的交易、订单、库存、发货等数据，供应链环节的供应商产品、可供货状况、质量、价格等数据，仓储物流环节的库存、可用车辆、路线派遣等数据，也都存放到云端。")]),_._v(" "),v("p",[_._v("这时候，我们可以看到，云端的所有数据完整映射了企业实体的采购、供应、生产、物流、市场、销售、服务等各个环节的真实状态。通过建立数据模型，我们将这些跨部门的数据进行整合应用，就能够使企业整体的效率达到最佳。")]),_._v(" "),v("p",[_._v("这种将整个价值链的实体操作状态数据传送到云端，在云端通过数据模型建立所有环节之间关系的数字化模型与数据应用，我们称之为 CPS（信息物理系统）。")]),_._v(" "),v("p",[_._v("有人可能会说，CPS 不就是工业 4.0 的标准架构吗？它也可以应用在非生产制造型企业吗？当然可以！工业 4.0 的目标就是要达成企业智能化，它定义出虚实整合的架构，也定义了不同领域企业、不同层级数据传输的标准，我们只需要在应用时考虑所在行业的具体场景。")]),_._v(" "),v("p",[_._v("我曾在《商业评论》发表过一篇"),v("a",{attrs:{href:"http://mp.weixin.qq.com/s?__biz=MjM5ODI3NDM4MA==&mid=2651629368&idx=1&sn=3e8909f7f141a44de85a6b11bac4493a&chksm=bd35331b8a42ba0d144946053b62abd26e11a42e0273fca78b121c4b7527beeacc8bab39ccd7&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"}},[_._v("《上汽大通：C2B 数字化修炼之路》"),v("OutboundLink")],1),_._v("的案例。上汽大通通过前端的蜘蛛智选 App 获取客户个性化的购车订单，在确认客户订单的同时，蜘蛛智选即可告诉客户什么时候能够交车。")]),_._v(" "),v("p",[_._v("之所以能够做到这一点，就是因为上汽大通将客户订单需求、生产排程、原材料供应等整个价值链的数据完全打通，这是一个典型的企业级别的智能化案例。")]),_._v(" "),v("p",[_._v("再看一个工程机械企业的案例。卡特彼勒是一家全球知名的工程设备公司，它在自己生产的工程设备上安装了传感器，可以将设备在使用过程中的状态数据实时回传，然后根据这些数据安排设备维修、零部件更换等。")]),_._v(" "),v("p",[_._v("基于此，卡特彼勒将自己的商业模式从销售工程设备转型为提供按设备使用时间付费的工程服务。")]),_._v(" "),v("p",[_._v("对于客户来说，由于工程设备不是自己购买的，就不需要花精力做维修保养，更不需要担心因为设备维修保养而影响设备可用率和工期。")]),_._v(" "),v("p",[_._v("对于卡特彼勒来说，同一台设备可以提供给多个客户使用，实际总收费增加了，而且可以主动安排设备维修，就不需要担心客户突然把设备送厂维修，而厂内的维修工位或技师都没空的尴尬情况，维修效率因此可以大幅提升，维修人员成本也可以显著降低。这就是打通数据链后可以实现的智慧型企业。")]),_._v(" "),v("h2",{attrs:{id:"第五阶段-生态整合"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#第五阶段-生态整合"}},[_._v("#")]),_._v(" "),v("strong",[_._v("第五阶段：生态整合")])]),_._v(" "),v("p",[_._v("前一阶段的智慧型企业看起来已经发展到了数字化的极致，下一步还能做什么呢？")]),_._v(" "),v("p",[_._v("想一想，当你面对客户时，在很多情况下，是不是需要其他企业配合协作？这种分工协作就产生了一个时髦的词——"),v("strong",[_._v("生态圈")]),_._v("。其实，生态圈现象由来已久，严格来说，几乎所有企业都存在于某个生态圈之中，只是过去缺乏信息对接，使得原本彼此紧密的关系链被切断了。")]),_._v(" "),v("p",[_._v("例如，生产冰箱、电视机的家电企业与生产芯片的企业有没有关系？家电企业与各地的购物中心、百货商场有没有关系？当然有关系，因为家电企业的上游就有芯片企业，下游就有百货商场。")]),_._v(" "),v("p",[_._v("你会说确实有关系，可是不紧密啊。是的，在平时你确实感觉不到它们之间关系紧密，但在出现芯片短缺时你还感觉不到吗？这就是为什么美国封锁半导体供应链时，其破坏力可能殃及全球各地的各种行业，因为全球供应链就是一个生态圈。")]),_._v(" "),v("p",[_._v("我们平时在网上购物，下单后会开始关注商家什么时候发货、什么时候能收到货，这就形成了一个由电商平台、销售产品的商家、物流企业构成的生态圈。")]),_._v(" "),v("p",[_._v("商家通过电商平台接收到订单，然后将订单信息传送给自己的仓储中心，仓库拣货后将物流企业的快递单号扫入自己的仓库管理系统，这时候，我们就可以查询到订单发货以及后续的物流中转实时信息。")]),_._v(" "),v("p",[_._v("在过去，由于缺乏信息整合，下单后一直到送货上门的整个流程是无法可视化的，但是通过生态圈的数据打通，全程可视化就变得很容易。")]),_._v(" "),v("h1",{attrs:{id:"_02-应用五阶段模型的注意点"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_02-应用五阶段模型的注意点"}},[_._v("#")]),_._v(" "),v("strong",[_._v("02 应用五阶段模型的注意点")])]),_._v(" "),v("p",[_._v("企业数字化五阶段模型是根据众多案例实践和调研总结而成的经验法则，我们在应用这个模型时需要了解和关注它的三个特点。")]),_._v(" "),v("h2",{attrs:{id:"_1-发展连续性"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-发展连续性"}},[_._v("#")]),_._v(" "),v("strong",[_._v("1. 发展连续性")])]),_._v(" "),v("p",[_._v("数据采集是所有企业数字化、智能化的基础。有了数据采集和连通，企业价值链环节才可能实现局部的运营监控。能够监控，才可能发现问题，做出调整，实现自主优化。等到所有环节都能做到自主优化，整个企业就实现了智能化。当一个生态圈内的每个企业都智能化了，整个生态圈也就智能化了。")]),_._v(" "),v("p",[_._v("可见，数字化是一个连续发展的历程，各个阶段不能断开，也不能跳跃式推进，必须实实在在地一个阶段一个阶段向前走，才能积累出强大的数字能力。")]),_._v(" "),v("p",[_._v("就我所观察的国内大型企业来看，无论是生产制造企业、物流供应链企业，还是零售企业，大部分都已经能够实时采集数据。当然，在一些传统大型制造企业，仍然有许多旧型号的生产设备无法进行实时的数据采集，所以应该持续更新这些生产设备，或者给它们加装可以采集数据的物联网设备。对于中小型制造企业，则有更多的生产设备有待升级。")]),_._v(" "),v("p",[_._v("零售行业也存在类似的状况。大型零售企业在前端大都已经采用智慧型收银系统（POS），而中小型零售企业相对滞后，仍无法实时采集销售数据。")]),_._v(" "),v("h2",{attrs:{id:"_2-覆盖延展性"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_2-覆盖延展性"}},[_._v("#")]),_._v(" "),v("strong",[_._v("2. 覆盖延展性")])]),_._v(" "),v("p",[_._v("从企业数字化五阶段模型可以看出，数字化的前三个阶段都聚焦在价值链环节的局部功能上，第四阶段开始关注整个价值链的数据整合和应用，到了第五阶段甚至超越了企业的边界，扩展到整个生态圈。")]),_._v(" "),v("p",[_._v("所以，企业数字化的覆盖范围是具有延展性的，企业在规划自己的数字化进程时，需要从小到大逐步扩大范围。")]),_._v(" "),v("h2",{attrs:{id:"_3-价值递增性"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_3-价值递增性"}},[_._v("#")]),_._v(" "),v("strong",[_._v("3. 价值递增性")])]),_._v(" "),v("p",[_._v("在企业数字化五阶段模型中，对于作为基础的数据采集，还无法计算其价值，因为数据还没有加以利用。但是，从第二阶段开始，通过数据比对分析，能够对企业运营中出现的问题提出警示。")]),_._v(" "),v("p",[_._v("到了第三阶段，能够帮助制定方案，排除问题，比如在生产环节确保产品质量，在物流环节提高效率，在销售环节避免客户流失。")]),_._v(" "),v("p",[_._v("在第四阶段，因为整合了价值链所有环节的信息，就能够全面提升企业的运营效益，甚至实现商业模式创新，快速提升企业的竞争力和获利能力（比如卡特彼勒的案例）。")]),_._v(" "),v("p",[_._v("到了第五阶段，产生的价值更大，因为企业能够更有效地利用外部资源加强对客户的覆盖，提升客户价值。所以，在企业数字化五阶段模型中，越到后面的阶段，数字化价值就越大。")]),_._v(" "),v("p",[_._v("随着人工智能、物联网、云计算、移动互联网等技术的迭代创新，技术对企业数字化赋能的能力更加显著。")]),_._v(" "),v("p",[_._v("面对未来的发展与竞争，企业势必要在数字技术应用上做更多的投入，才能更有效地连接内外部资源，给客户提供更有价值的服务，创造自身更强大的竞争力。")]),_._v(" "),v("p",[_._v("本文提出的企业数字化五阶段模型，在生产制造、物流、零售、医疗大健康等行业，已经有了很多成功案例。你不妨试着用这个模型思考一下，自己企业的数字化到了什么阶段，未来可以怎么走。")])])}),[],!1,null,null,null);v.default=s.exports},962:function(_,v,t){_.exports=t.p+"assets/img/640-1212121212dsadpisdaw.d270b4cc.png"}}]);